const GET_DEPARTMENTS = 'get/departments'
const GET_PRODUCTS_BY_DEPT = 'get/department/product'

/* ___________ A C T I O N S   ___________ */

export const getDepartments = (departments) => {
    return {
        type: GET_DEPARTMENTS,
        departments
    };
};

export const getProducts = (products) => {
    return {
        type: GET_PRODUCTS_BY_DEPT,
        products
    };
}

/* ___________ T H U N K S   ___________ */

// Get all departments
export const getDepartmentsThunk = () => async (dispatch) => {
    const response = await fetch('/api/departments');
    const departments = await response.json();
    console.log('departments', departments)
    dispatch(getDepartments(departments))
}

// Get products for department 
export const getProductsThunk = (departmentId) => async(dispatch) => {
    const response = await fetch(`/api/:departmentId/products`)
    const products = await response.json()
    dispatch(getProducts(products))
}

/* ___________ R E D U C E R ___________ */
const departmentsReducer = (state = {}, action) => {
    let newState = {};
    switch (action.type) {
        case GET_DEPARTMENTS:
            action.departments.forEach(ele => newState[ele.id] = ele)
            return newState


        default:
            return state;
    }
}

export default departmentsReducer;
