import React, { useEffect } from 'react';
import { useHistory } from 'react-router-dom'
import LogoutButton from './auth/LogoutButton';
import './navbar.css'

export default function ProfileDropdown({ openMenu2, setOpenMenu2 }) {
    const history = useHistory();

    useEffect(() => {
        if (!openMenu2) return

        const closeMenu = () => setOpenMenu2(false);
        document.addEventListener('click', closeMenu)



        return () => document.removeEventListener('click', closeMenu)
    }, [openMenu2]);

    return (
        <div className="dropdownMainDiv2" >
            <ul className="dropdownUl2" >
                <li onClick={() => history.push('/login')}> Login </li>
                <li onClick={() => history.push('/departments/2')}> Logout </li>
                {/* <li onClick={() => history.push('/departments/3')}> Sports & Outdoors </li> */}
                {/* <LogoutButton /> */}
                <li onClick={() => history.push('/departments/4')}> Movies & Shows </li>
                <li onClick={() => history.push('/departments/5')}> Personal Care </li>
                <li onClick={() => history.push('/departments/6')}> Kitchen & Dining </li>
            </ul>
        </div>
    )
}
