import './index.css';
import { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { getOneReviewThunk } from '../../store/review';
import { getProductThunk } from '../../store/product';
import { editReviewThunk } from '../../store/review';

export default function EditReview() {
    // Grab user of the session
    let sessionUserId = useSelector(state => state.session.user.id);
    const review = Object.values(useSelector(state => state.reviews));
    const product = useSelector(state => state.products)[1];
    const [comment, setComment] = useState('');
    const [rating, setRating] = useState('');
    const [validationErrors, setValidationErrors] = useState([]);
    const productId = review[2]
    if (product) console.log('product', product.id)
    const dispatch = useDispatch();
    const { reviewId } = useParams();


    useEffect(() => {
        dispatch(getOneReviewThunk(reviewId))
        dispatch(getProductThunk(productId));
        setValidationErrors(validationErrors);
    }, [productId]);


    const handleSubmit = async (e) => {
        e.preventDefault();
        const review = {
            rating,
            comment
        };
        setComment('');
        setRating(1);

        let reviewDispatch = await dispatch(editReviewThunk(review));
        if (reviewDispatch) {

        };
    };
    if (!review) return null
    return (
        <form className='editReviewForm' onSubmit={handleSubmit}>

            <h2 className='reviewH2'>Edit your review </h2>
            <ul className='createReviewErrors'>
                {validationErrors.map((error, idx) => (
                    <li key={idx}>{error}</li>
                ))}
            </ul>
            <label>
                <textarea
                    className='reviewTextArea'
                    placeholder='Write a review'
                    type="text"
                    value={review[0]}
                    onChange={(e) => setComment(e.target.value)}
                    required
                />
            </label>
            <label>
                Stars
                <select
                    className='starsSelect'
                    onChange={(e) => setRating(e.target.value)}
                    value={review[3]}
                    required
                >
                    <option value={1}>1</option>
                    <option value={2}>2</option>
                    <option value={3}>3</option>
                    <option value={4}>4</option>
                    <option value={5}>5</option>
                </select>
            </label>
            <button type='submit' className='editReviewButton' disabled={validationErrors.length > 0}> Edit </button>
        </form>

    );
};
