import './index.css';
import { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { getOneReviewThunk } from '../../store/review';

export default function EditReview() {
    // Grab user of the session
    let sessionUserId = useSelector(state => state.session.user.id);
    const review = Object.values(useSelector(state => state.reviews));
    const [comment, setComment] = useState('' || review.comment);
    const [rating, setRating] = useState(1);
    const [validationErrors, setValidationErrors] = useState([]);

    const dispatch = useDispatch();
    const { reviewId } = useParams();


    // const reviewUserIds = revsArr.map(review => review.userId);
    // let validationBoolean = false;

    // for (let i = 0; i < reviewUserIds.length; i++) {
    //     if (parseInt(sessionUserId) === parseInt(reviewUserIds[i])) {
    //         validationBoolean = true
    //     };
    // };

    useEffect(() => {
        const validationErrors = [];
        // if (!review && !validationBoolean) validationErrors.push('Please provide a lengthier review');
        // if (validationBoolean) validationErrors.push("You can't make two reviews for the same spot!")
        if (comment.length > 1000) validationErrors.push('Please provide a review with less than 1000 characters');
        // dispatch(getProductReviewsThunk(productId));
        dispatch(getOneReviewThunk(reviewId))
        setValidationErrors(validationErrors);
    }, [comment]);

    const handleSubmit = async (e) => {
        e.preventDefault();
        const review = {
            rating,
            comment
        };
        setComment('');
        setRating(1);

        let reviewDispatch = await dispatch();
        if (reviewDispatch) {

            // get reviews again after we create review
            // await dispatch(getReviewsThunk(spotId));
            // await dispatch(getSpotByid(spotId));
        };
    };

    return (
        <form className='editReviewForm' onSubmit={handleSubmit}>
            <h2 className='reviewH2'>Edit your review </h2>
            <ul className='createReviewErrors'>
                {validationErrors.map((error, idx) => (
                    <li key={idx}>{error}</li>
                ))}
            </ul>
            <label>
                <textarea
                    className='reviewTextArea'
                    placeholder='Write a review'
                    type="text"
                    value={comment}
                    onChange={(e) => setComment(e.target.value)}
                    required
                />
            </label>
            <label>
                Stars
                <select
                    className='starsSelect'
                    onChange={(e) => setRating(e.target.value)}
                    value={rating}
                    required
                >
                    <option value={1}>1</option>
                    <option value={2}>2</option>
                    <option value={3}>3</option>
                    <option value={4}>4</option>
                    <option value={5}>5</option>
                </select>
            </label>
            <button type='submit' className='editReviewButton' disabled={validationErrors.length > 0}> Edit </button>
        </form>

    );
};
